import React, { useContext, useState } from "react";
import './Navbar.css';
import logo from '../Assets/logo.png'
import cart_icon from '../Assets/cart_icon.png'
import { Link } from "react-router-dom";
import { ShopContext } from "../../Context/ShopContext";
import nav_downarrow from '../Assets/nav_downarrow.png'
const Navbar = () =>{


    //using usestate because when we click then it go to menu session
    const [menu,setMenu]= useState("shop");
    const {getTotalCartItems} = useContext(ShopContext);
    const menuRef = useRef();

    const dropdown_toggle = (e) =>{
        menuRef.current.classList.toggle('nav-menu-visible');
        e.target.classList.toggle('open');

    }

    return(
        <div className="navbar">
            <div className="nav-logo">
                <img onClick={dropdown_toggle} src={logo}  alt="" />
                <p>SHOPPER</p>
            </div>
            <img src={nav_downarrow} alt="" />
            <ul ref={menuRef} className="nav-menu">
                                                     {/* usig ternary operator if we click the menu button then it presents shop then active hr tag or not then empty tag  */}
                <li onClick={()=>{setMenu("shop")}}><Link style={{textDecoration: 'none'}} to='/'>Shop</Link>{menu==="shop"?<hr/>:<></>}</li>
                <li onClick={()=>{setMenu("mens")}}><Link style={{textDecoration: 'none'}} to='/mens'>Men</Link>{menu==="mens"?<hr/>:<></>}</li>
                <li onClick={()=>{setMenu("womens")}}><Link style={{textDecoration: 'none'}} to='/womens'>Women</Link>{menu==="womens"?<hr/>:<></>}</li>
                <li onClick={()=>{setMenu("kids")}}><Link style={{textDecoration: 'none'}} to='/kids'>Kids</Link>{menu==="kids"?<hr/>:<></>}</li>
            </ul>
            <div className="nav-login-cart">
               <Link to='/login'><button>Login</button></Link> 
               <Link to='/cart'><img src={cart_icon} alt="" /></Link>
                <div className="nav-cart-count">{getTotalCartItems()}</div>
            </div>
         
        </div>
    )
}

export default Navbar
